thisKnob = nuke.thisKnob()

allGroups = ['JPEG_Options','PNG_Options','TIFF_Options','EXR_Options','DPX_Options']

def hideKnobs():
 for knobs in allGroups:
 thisNode.knob(knobs).setVisible(False)

if thisKnob.value() == 'None':
 hideKnobs()
if thisKnob.value() == 'JPEG':
 hideKnobs()
 thisNode.knob('JPEG_Options').setVisible(True)
if thisKnob.value() == 'PNG':
 hideKnobs()
 thisNode.knob('PNG_Options').setVisible(True)
if thisKnob.value() == 'TIFF':
 hideKnobs()
 thisNode.knob('TIFF_Options').setVisible(True)
if thisKnob.value() == 'EXR':
 hideKnobs()
 thisNode.knob('EXR_Options').setVisible(True)
if thisKnob.value() == 'DPX':
 hideKnobs()
 thisNode.knob('DPX_Options').setVisible(True)
